import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    value: "Ahmet",
}
const reminderArray = [];
export const reminderSlice = createSlice({
    name: "reminderAdder",
    initialState,
    reducers: {
        add: (state) => {
            reminderArray.push(state.value)
        }
    }
});

export const { add } = reminderArray.actions

export default reminderArray.reducer;
